using System;
using System.IO;

namespace gDNA_Plasmid_FASTA
{
    class Program
    {
        static void Main(string[] args)
        {
            bool exit = false;
            while (exit == false)
            {
                Console.WriteLine("Enter the directory you want that FASTA saved:");
                string directory = Console.ReadLine();

                Console.WriteLine("Welcome to the FASTA File Creator for CRISPR plasmids.\nPlease enter the guide sequence: ");
                string sequence = Console.ReadLine();

                Console.WriteLine("Please enter the guide sequence name (eg. gND###): ");
                string guide_name = Console.ReadLine();

                Console.WriteLine("Please enter the target locus: ");
                string locus = Console.ReadLine();

                string fasta_header = String.Format("PG2_{0}_PTS_[pENO1_Cas9_pPtefTT]_PARS1_{1}_kan", guide_name, locus);



                string preguide_seq = "";
                string postguide_seq = "";

                string fasta_full = String.Format(">{0}\n{1}{2}{3}", fasta_header, preguide_seq, sequence, postguide_seq);
                string fasta_file_name = String.Format("{0}.fa", fasta_header);
                Console.WriteLine(fasta_full);

                using (StreamWriter outputFile = new StreamWriter(Path.Combine(directory, fasta_file_name)))
                {

                    outputFile.WriteLine(fasta_full);
                }


                Console.WriteLine("Press Enter to restart the script or E to exit");
                string input = Console.ReadLine();
                if ((input == "E") || (input == "e"))
                {
                    exit = true;
                }

            }





        }

    }

}
